import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  root: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 10,
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    alignSelf: "flex-start",
  },
  optionContainer: {
    borderColor: "lightgrey",
    borderWidth: 2,
  },
  optionImage: {
    width: 100,
    height: 100,
  },
  optionText: {
    width: 100,
    height: 100,
  },
});

export default styles;
