import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  root: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 10,
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    // backgroundColor: "red",
    alignSelf: "stretch",
  },
  optionsContainer: {
    width: "100%",
    flexWrap: "wrap",
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-between",
    alignContent: "space-between",
  },
  optionContainer: {
    borderColor: "lightgrey",
    borderWidth: 2,
    borderRadius: 10,
    borderBottomWidth: 4,

    width: "48%",
    height: "48%",
  },
  optionImage: {
    width: 100,
    height: 100,
  },
  optionText: {
    width: 100,
    height: 100,
  },
});

export default styles;
